-- ----------------------------
-- Table structure for ali_article
-- ----------------------------
DROP TABLE IF EXISTS `ali_article`;
CREATE TABLE `ali_article` (
  `article_id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '文章id',
  `article_title` varchar(30) NOT NULL COMMENT '文章标题',
  `article_body` text CHARACTER SET utf8 COLLATE utf8_general_ci COMMENT '文章内容',
  `article_adminid` int(10) unsigned NOT NULL COMMENT '文章作者',
  `article_cateid` tinyint(3) unsigned NOT NULL COMMENT '所属栏目',
  `article_slug` varchar(255) DEFAULT NULL COMMENT '文章别名',
  `article_cover` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL COMMENT '文章封面图',
  `article_addtime` datetime DEFAULT NULL,
  `article_status` enum('已发布','草稿') CHARACTER SET utf8 COLLATE utf8_general_ci DEFAULT NULL,
  `article_file` varchar(255) DEFAULT NULL,
  `article_desc` varchar(200) DEFAULT NULL,
  `article_click` int(11) DEFAULT NULL,
  `article_good` int(11) DEFAULT NULL,
  `article_bad` int(11) DEFAULT NULL,
  `article_cmt` varchar(100) DEFAULT NULL,
  `article_focus` tinyint(4) DEFAULT NULL,
  PRIMARY KEY (`article_id`),
  UNIQUE KEY `article_title` (`article_title`)
) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

